def get_gender(name: str) -> int:
    """
    Получает имя, возвращает пол. 0 - Ж, 1 - М, -1 - не удалось определить
    """
    lower_name = name.lower()
    if lower_name in fem_and_male_names:
        return -1
    if lower_name in male_names:
        return 1
    if lower_name in female_names:
        return 0
    return -1

